sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var what = g_form.getValue('u_request_type');

	g_form.clearOptions('u_what_needed');

	if (what == 'hr') {

		g_form.addOption('u_what_needed', 'hr1', "Humans 1");
		g_form.addOption('u_what_needed', 'hr2', "Humans 3");
		g_form.addOption('u_what_needed', 'oto', "Others");

	} else if (what == 'facilities') {

		g_form.addOption('u_what_needed', 'facilities1', "Fanny 1");
		g_form.addOption('u_what_needed', 'facilities2', "Fanny 2");
		g_form.addOption('u_what_needed', 'oto', "Others");
	} else {
		g_form.addOption('u_what_needed', 'legal1', "Legal 1");
		g_form.addOption('u_what_needed', 'legal2', "Legal 2");
		g_form.addOption('u_what_needed', 'oto', "Others");
	}


   //Type appropriate comment here, and begin script below

}
